INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (2016,580,3,'Официальные результаты - это выигрышные номера, номера победителей, и призы каждой категории, опубликованные официальным оператором лотереи.','ru');
