INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (2020,701,3,'Сайт Euromillions.com предоставляет официальную информацию о международных лотерейный тиражах, включая выигрышные номера, официальные правила и результаты.','ru');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (2021,702,3,'Любая информация на сайте Euromillions.com публикуется исключительно в целях предоставления её участникам лотереи, и ни для чего больше. Информация, поступающая в операционную систему веб-сайта Euromillions.com, иногда может содержать ошибки и неточности, которые были совершенны случайно, без всякого злого умысла. .','ru');
