INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (2025,716,3,'Никто не имеет права копировать или дублировать в какой бы то ни было форме и в каких бы то ни было средствах массовой информации какую-либо часть настоящих Условий и Положений или правил, представленных на веб-сайте, а также игр, предложенных на нём. ','ru');
