INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (2044,719,3,'В случае выяснения, жалобы, спора, оговорки, претензии или действия, возникающего в результате участия в какой-либо деятельности на веб-сайте Euromillions.com, которые в дальнейшем не решатся между веб-сайтом Euromillions.com и зарегистрированным пользователем, спор будет передан на разбирательство правовой юрисдикции, в которой зарегистрирована компания Euromillions.com.','ru');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (2045,720,3,'Правовые отношения между зарегистрированным пользователем и Euromillions.com регулируются законом о правовой юрисдикции там, где зарегистрирована компания Euromillions.com, за исключением нормы о международных гражданских правах. Все споры и недопонимания, связанные с лотерейной игрой или возникающие в результате игры, разрешаются только компетентным судом, учитывающим местную и предметную юрисдикцию в том месте, где компания Euromillions.com зарегистрирована. ','ru');
