INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (2048,641,3,'Цель игры состоит в том, чтобы попытаться угадать выигрышные номера лотерейного билета, который разыгрывается и публикуется официальным оператором лотереи. Билеты с совпавшими номерами, призовые категории таких билетов и призы также устанавливаются в соответствии с публикациями официальных операторов лотерей.','ru');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (2049,642,3,'Пользователи могут выиграть различные денежные призы, равные тем, что разыгрываются в официальных лотерейных розыгрышах. Как указано в требованиях к участию, участие в игре должно соответствовать официальным и действующим правилам каждой официальной лотерейной игры.','ru');
