INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (2224,746,1,'The system under which the tickets of El Gordo Christmas lottery are sold is different from the one you are used to on EuroMillions, but it\'s really easy:\r\n<ol style=\'margin-left: 30px;\'><li>Choose you favourite 5-digit number from 00000 to 99999, from the tickets available.</li>\r\n<li>Choose how many tickets you want to play.</li>\r\n<li>Once you have selected your tickets click on Next to pay them.</li></ol>','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (2225,746,2,'El sistema bajo el cual se venden los décimos de El Gordo de Navidad es diferente al cual estás acostumbrado en EuroMillones, pero es muy fácil. \r\n<ol style=\'margin-left: 30px;\'><li>Elige tus 5 números favoritos del 00000 al 99999, de los décimos disponibles. </li>\r\n<li>Elige cuántos décimos quieres jugar.</li>\r\n<li>Una vez que hayas elegido tus décimos selecciona Siguiente para realizar el pago.</li></ol>','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (2226,746,3,'Система, по которой продаются билеты лотереи «Эль Гордо де Навидад», отличается от системы Евромиллиона, но она ничуть не сложнее:\r\n<ol style=\'margin-left: 30px;\'><li>Выберите любимый 5-значный номер от 00000 до 99999, из доступных на сайте билетов</li>\r\n<li>Выберите, сколько билетов вы хотите приобрести.</li>\r\n<li>Как только вы выбрали свои билеты, нажмите «Далее», чтобы оплатить их.</li></ol>','ru');
