INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3016,920,1,'La Pedrea is one of the most common combinations in every draw of the Spanish Christmas Lottery and consists of 5 correct digits of the winning number. This way, more players have the chance of taking a prize home, as each Pedrea combination delivers €1,000.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3017,920,2,'La Pedrea es una de las combinaciones más comunes en cada sorteo del Gordo de Navidad y consiste en 5 aciertos del número ganador. De esta forma más jugadores tienen oportunidad de llevarse algún premio a casa, ya que por cada décimo acertado se entrega €1,000.','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3018,920,3,'Ла Педреа- одна из самых распространенных комбинаций в каждом розыгрыше Рождественской лотереи и состоит она из 5 правильно угаданных номеров- главного победителя. Таким образом, у большого количества игроков есть возможность забрать домой приз, так как 1000 евро присуждается за каждую верную комбинацию.','ru');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3019,920,4,'La Pedrea è una delle combinazioni più comuni in ogni estrazione della lotteria di Natale e consiste in 5 cifre corrispondenti del numero vincente. In questo modo molti più giocatori hanno l\'opportunità di aggiudicarsi un premio, dato che per ogni decimo si ottengono €1,000.','it');
