INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3024,922,1,'All winnings that you get on the Spanish Christmas Lottery draw will be credited to your player account on EuroMillions.com shortly after the draw. Big prizes will be directly transferred to your private bank account without commissions.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3025,922,2,'Todos los premios que obtengas del sorteo del Gordo de Navidad serán acreditados en tu cuenta de jugador de EuroMillions.com poco después del sorteo. Los premios de gran valor serán directamente transferidos a tu cuenta bancaria sin ninguna comisión.','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3026,922,3,'Все призы, которые вы выиграете в Рождественскую лотерею будут зачислены на ваш счет игрока на сайте EuroMillions.com вскоре после розыгрыша. Крупные выигрыши будут напрямую перечислены на ваш банковский счет без какой-либо комиссий.','ru');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3027,922,4,'Tutti i premi che vinci con l\'estrazione della lotteria di Natale saranno accreditati sul tuo conto di EuroMillions.com poco dopo la conclusione dell\'estrazione. I premi di grande valore saranno trasferiti direttamente sul tuo conto bancario senza alcuna commissione.','it');
