INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3284,478,4,'Scegli i tuoi numeri','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3285,479,4,'Completa una combinazione dell\'EuroMillions selezionando <em>cinque</em> numeri principali dall\'1 al 50 e <em>due</em> numeri stella dall\'1 al 12 per ogni linea. In alternativa, clicca \'Numeri random in tutte le linee\' per generare una sequenza casuale di numeri.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3286,480,4,'Crea la tua combinazione','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3287,484,4,'Visualizzerai 6 linee per giocata in uno schermo standard di computer fisso o portatile (di meno se accedi con uno schermo più piccolo, con un tablet o un cellulare). Per aggiungere più linee, clicca su \'Aggiungi linee\' e appariranno nuove combinazioni di gioco.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3288,481,4,'Compra i tuoi biglietti','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3289,485,4,'Quando sei soddisfatto della tua scelta di numeri clicca su \'Continua\' e verrai guidato attraverso il processo di acquisto.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3290,482,4,'Aspetta l\'estrazione','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3291,486,4,'Una volta effettuato il pagamento, riceverai un messaggio con la conferma dei numeri che hai giocato e dell\'estrazione a cui parteciperai. Puoi vedere tutte le linee e i biglietti che hai comprato in ogni momento nel tuo account di giocatore.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3292,483,4,'Controlla i risultati','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3293,487,4,'Immediatamente dopo l\'estrazione riceverai un\'email con il dettaglio degli ultimi risultati, in cui ti sarà comunicato se hai vinto. Tutti le vincite verranno automaticamente accreditate sul tuo account di giocatore.','it');
