INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3342,563,4,'EuroMillions.com è il primo sito web per giocare al lotto costruito per funzionare su ogni dispositivo e ogni formato di schermo, piccolo o grande che sia. Smartphone o desktop, ti offriremo sempre la miglior esperienza possibile. Diamo valore al tuo tempo e lavoriamo per offrirti una veloce, affidabile e semplice esperienza di gioco online dal comfort di casa tua o da dovunque tu sia. Ascoltiamo le tue necessità e ci assicuriamo che le tue vincite siano sempre prive di commissioni.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3343,564,4,'EuroMillions è la prima lotteria trans-nazionale in Europa, creata nel 2004. EuroMillions.com è un team internazionale composto da esperti ed appassionati giocatori: siamo convinti che per offrirti il miglior servizio, dobbiamo seguire tre importanti principi, essere veloci, convenienti e sicuri. Speriamo davvero che giocando con noi si realizzino i tuoi sogni. Per i meno fortunati, speriamo che l\'emozione di immaginarsi una vita con un gran premio della lotteria vi faccia passare dei piacevoli momenti sognando fino al giorno in cui vincete davvero e tutto ciò che avete immaginato diventa realtà. Le estrazioni dell\'EuroMillions si svolgono ogni Martedì e Venerdì sera alle 20:45 CET a Parigi, in Francia.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3344,623,4,'Riservatezza','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3345,624,4,'I dati dei clienti sono strettamente confidenziali e saranno utilizzati esclusivamente da EuroMillions.com, fatte salve le eccezioni contenute in questi Termini e Condizioni o in base alla legge applicabile.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3346,625,4,'La Società aderisce alla legge sulla protezione dei dati, ad altri regolamenti pertinenti, alle note legali e/o simili nel luogo del contratto, tenendo conto della direttiva sulla protezione dei dati (direttiva 95/46 / CE) e della direttiva sulla riservatezza delle comunicazioni elettroniche (direttiva CE) / 2002/58 / CE).','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3347,626,4,'EuroMillions.com garantisce ai titolari dei conti che, in qualsiasi momento:','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3348,627,4,'tutti i dati personali sono trattati in conformità con i diritti del titolare dell\'account in questione;','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3349,636,4,'tutte le misure ragionevoli sono prese per completare, correggere, bloccare o cancellare i dati nella misura in cui tali dati sono incompleti o errati, tenendo conto delle finalità per cui sono trattati.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3350,628,4,'tutti i dati personali sono trattati in modo equo e lecito;','it');
