INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3358,637,4,'EuroMillions.com divulga i dati personali su ordine delle autorità governative e / o in base alla normativa vigente. Inoltre, la Società si riserva il diritto di divulgare i dati personali ai destinatari rilevanti quando la Società ha ragionevoli motivi per sospettare le irregolarità che coinvolgono un conto scommesse bet-at-home.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3359,638,4,'EuroMillions.com si riserva il diritto di trattare i dati personali per finalità di CRM (Customer Relationship Management).','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3360,694,4,'Valuta','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3361,695,4,'La valuta operativa del sito EuroMillions.com è l\'EURO. Le transazioni di addebito del prezzo di partecipazione e il pagamento del montepremi ai vincitori saranno effettuati in EURO.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3362,696,4,'In ogni caso in cui un\'estrazione ufficiale della lotteria viene condotto in una valuta diversa dall\'EURO, per la pubblicazione sul sito web EuroMillions.com del valore dei premi in tale estrazione, verrà effettuata una conversione approssimativa dalla valuta in cui dovrebbero essere pagati all\'EURO.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3363,697,4,'Tutti gli scambi da diverse valute a EURO saranno effettuati utilizzando i tassi di conversione correnti che vengono periodicamente pubblicati dagli istituti bancari.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3364,698,4,'Le vincite in valute diverse dall\'Euro verranno prima convertite in EURO sulla base dell\'ultimo tasso di cambio pubblicato periodicamente e poi accreditate sul conto del giocatore.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3365,699,4,'Il sito web EuroMillions.com ha il diritto di detrarre le spese ragionevoli derivanti dai costi di cambio valuta.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3366,610,4,'Dichiarazione dei partecipanti','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3367,611,4,'Con l\'accettazione dei presenti Termini e Condizioni, il partecipante dichiara che, in base alle leggi del paese in cui è cittadino, ha diritto a partecipare al gioco che si svolge su questo sito. Il partecipante dichiara inoltre che, durante l\'utilizzo di questo sito Web, lui / lei agisce per conto suo e che non vi è altra persona che sia rappresentata da lui / lei.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3368,571,4,'DEFINIZIONI','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3369,572,4,'Le Informazioni di accesso si riferiscono alle credenziali di accesso univoche generate durante il processo di registrazione.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3370,581,4,'Biglietto fa riferimento al biglietto elettronico che viene compilato sul sito Web EuroMillions.com. I biglietti sono composti da una serie di campi.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3371,582,4,'I Numeri vincenti indicano una serie di numeri di un determinato gioco, estratti in un determinato giorno e pubblicati in modo definitivo dall\'operatore ufficiale della lotteria.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3372,573,4,'L\'Account giocatore è un insieme di pagine Web dedicate agli utenti registrati, utilizzate per gestire i loro dati di identificazione e accesso su EuroMillions.com.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3373,574,4,'Termine di spedizione o Tempo di chiusura indica il tempo rimanente fino a quando il sito web di EuroMillions.com accetta acquisti per la prossima estrazione per un determinato gioco. La scadenza si basa sull\'ora locale, sulle regole locali del gioco specifico e sugli orari di apertura locali dei negozi della lotteria locale.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3374,575,4,'Campo indica un\'area singola, numerabile e cliccabile per selezionare i numeri che l\'utente desidera giocare. Un biglietto è composto da un insieme di campi.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3375,576,4,'90/5000\r\nGioco indica qualsiasi gioco individuale o di gruppo disponibile sul sito Web EuroMillions.com.','it');
