INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3497,569,4,'Panamedia BV, una società a responsabilità limitata privata organizzata e regolamentata dal governo di Curaçao (Regno dei Paesi Bassi) ed esistente nel registro commerciale di Curaçao Numero 135786. Panamedia BV opera con la licenza di gioco n. 5536 / JAZ rilasciata dal governatore di Curaçao. La Società è l\'operatore del sito web EuroMillions.com.','it');
