INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3938,1021,3,'Хотите узнать стали ли ваши номера выигрышными? Все последние результаты самых популярных лотерей в мире вы можете проверить на нашем сайте. И если вам не повезло на этот раз, не сдавайтесь, и вам обязательно повезёт в следующий раз вместе с EuroMillions.com!','ru');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3939,1021,4,'Stai aspettando di sapere se con i tuoi numeri hai vinto un premio? Verifica qui i risultati delle ultime estrazioni dei giochi del lotto più popolari al mondo, E se questa volta non hai avuto fortuna, non desistere e gioca di nuovo con EuroMillions.com!','it');
