INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4022,475,5,'Als u nog vragen of suggesties heeft met betrekking tot ons privacybeleid, neem dan contact met ons op: <a href=\'mailto:support@euromillions.com\'> support@euromillions.com </a>.','nl');
