INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4103,653,5,'Een ticket dat om welke reden dan ook, inclusief technische storingen, technische fouten, menselijke fouten, filtering van risicobeoordelingen, mogelijke fraudefilters, externe krachten of iets anders, niet met succes wordt geplaatst, wordt automatisch teruggestort op het spelersaccount van de deelnemer en geeft de deelnemer die het Ticket heeft ingevuld geen enkel recht, inclusief een beroep dat wordt ingediend voor de oorzaak van het falen of de gevolgen ervan.','nl');
