INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4175,712,5,'De Euromillions.com-website behoudt zich het recht voor om verdere toegang tot elke deelnemer en zijn betaalmiddelen onmiddellijk te verbieden, zonder kennisgeving, in het geval dat het deze algemene voorwaarden en in het bijzonder de regels die zijn vastgelegd in de wet- en regelgeving heeft overtreden. geciteerd in de Deelnamevereisten of wiens aanwezigheid en / of deelname juridische risico\'s of acties met zich meebrengt tegen de website Euromillions.com. In het geval van een dergelijk verbod, zal de Euromillions.com-website binnen een periode van dertig (30) dagen positieve saldi aan de deelnemer betalen.','nl');
