INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4468,541,5,'Speel snel en relax met EuroMillions.com','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4469,542,5,'Koop je loterij tickets voor de beste prijs','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4470,543,5,'Jou geld is veilig','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4471,547,5,'Miljoenen mensen spelen iedere dag mee met EuroMillions','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4472,544,5,'Deelnemers van EuroMillions.com hebben niet meer dan twee minuten nodig om in te loggen en hun aankopen te doen. Bij Euromillions.com kan je gewoon je favoriete nummers kiezen, achterover leunen en ontspannen.  Wij doen al het werk en controleren de loterij resultaten. Na de trekking ontvang je een e-mail met alle details over de laatste EuroMillions-resultaten. Als je gewonnen hebt worden de winsten automatisch bijgeschreven op je spelers account! ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4473,545,5,'Je kunt de loten die je hebt gekocht, de nieuwste <a href=\'/nl/euromillions/uitslagen\'>EuroMillions-resultaten</a> en de klantenservice allemaal op een plek vinden. Voor slechts € 3,00 (£ 2,51) per spel bieden we de beste online beschikbare prijs. Je winsten zijn altijd zonder commissies. Het maakt niet uit waar je woont - wij zijn een gereguleerde operator en onze services zijn wereldwijd beschikbaar, van Ierland tot Australië, van India tot Zuid-Afrika. Als je veel wint, staat ons team van professionals voor je klaar om ervoor te zorgen dat je de winst snel en veilig bij je thuis ontvangt. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4474,546,5,'Euromillions.com is een exploitant van een loterij met volledige licentie. We zijn klantgericht en streven voortdurend naar verbetering van onze prestaties om aan de eisen van onze klanten te voldoen en deze te overtreffen. Onze partnerschappen met de beste financiële instellingen en certificeringsbedrijven zorgen voor optimale veiligheid bij het spelen op onze website.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4475,559,5,'Pak je kans, speel de loterij','nl');
