INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4480,553,5,'Kansen','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4481,558,5,'Als je niet meespeelt, kan je niet winnen! ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4482,554,5,'Selecteer vijf nummers van 1 tot 50 en twee ‘Lucky Stars‘ genummerd van 1 tot 12. De resultaten worden kort na de trekking gepubliceerd op EuroMillions.com en spelers ontvangen een e-mailmelding met de laatste resultaten als ze gewonnen hebben. Winsten zijn belastingvrij en worden onmiddellijk bijgeschreven op het account van de speler op Euromillions.com','nl');
