INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4547,354,5,'EuroMillions is de grootste Europese transnationale loterij met een jackpot van minimaal 17 miljoen euro op elke dinsdag en vrijdag. De jackpot kan oplopen tot de 190 miljoen euro als er geen winnaars zijn.','nl');
