INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4565,371,5,'De EuroMillions Jackpot kan oplopen tot € 190 miljoen met een gegarandeerde jackpot van minimaal € 17 miljoen (£ 14,4 miljoen) per trekking (twee trekkingen per week). Bij afwezigheid van een jackpot-winnaar, wordt het geld doorgeschoven naar de volgende trekking en groeit het totdat de jackpot wordt gewonnen, of de Euromillions maximum prijs (€ 190 miljoen) wordt bereikt.','nl');
