INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4579,377,5,'Om in aanmerking te komen voor het spelen van EuroMillions of andere loterijen, moeten alle deelnemers 18 jaar of ouder zijn (16+ in het VK).','nl');
