INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4767,978,5,'De trekkingen van de belangrijkste loterij in Ierland, de Ierse Lotto, worden elke woensdag- en zaterdagavond om 20:00 lokale tijd in Dublin gehouden (21:00 uur Central Europe Time).','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4768,980,5,'De Franse Lotto is een van de meest populaire spellen in Frankrijk en heeft een trekking op iedere maandag, woensdag en zaterdag om 20:00 CET (21:00 GMT).','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4769,982,5,'De trekkingen van de nationale Oostenrijkse Lotto 6 aus 45 vinden twee keer per week plaats in Wien, op woensdag om 18:45 CET (17:45 GMT) en op zondag om 19:15 CET (18:15 GMT).','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4770,984,5,'De trekkingen van de Poolse Lotto, de grootste loterij van het land, vinden elke dinsdag-, donderdag- en zaterdagavond om 21:40 uur CET (20:40 GMT) plaats.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4771,986,5,'Een van de meest populaire loterijen in Australië, de OZ Lotto, heeft een keer per week een trekking. Iedere dinsdagavond om 20:30 AEST (21:30 AEDT). Dat komt overeen met 11.30 uur CET van oktober tot maart en 12.30 uur CET voor de rest van het jaar.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4772,988,5,'De Powerball heeft zijn eigen versie in Australië, die OZ Powerball wordt genoemd. De trekking van deze grote loterij vindt elke donderdag plaats om 20:30 AEST (21:30 AEDT), wat overeenkomt met 11:30 CET van oktober tot maart en 12:30 uur CET voor de rest van het jaar.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4773,990,5,'De OZ Monday and Wednesday lotto zijn beide in veel delen van Australië bekend als Gold Lotto en X Lotto. De trekkingen worden twee keer per week gehouden op maandag- en woensdagavond om 20:30 AEST / 21:30 AEDT, (11:30 CET van oktober tot maart en 12:30 CET voor de rest van het jaar). ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4774,992,5,'La Primitiva is een zeer populaire Spaanse loterij en de trekkingen vinden elke donderdag en zaterdag plaats in Madrid, Spanje, om 21:30 CET (20:30 GMT).','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4775,994,5,'El Gordo de la Primitiva is een Spaanse loterij met slechts één trekking per week, die wordt gehouden op zondagavond om 21:30 CET (20:30 GMT) in Madrid, Spanje.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4776,996,5,'BonoLoto is een Spaanse loterij die de deelnemers veel kansen geeft om te winnen. De trekking vindt bijna iedere plaats, van maandag tot zaterdag om 21:30 CET (20:30 GMT) in Madrid, Spanje.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4777,962,5,'Powerball is een van de meest populaire Amerikaanse loterijen en hun trekkingen vinden iedere woensdag en zaterdag plaats in Florida om 23.00 uur, Eastern Time (05.00 op donderdag en zondag in Centraal-Europese tijd).','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4778,964,5,'De trekkingen van de Pan-Europese loterij Eurojackpot worden elke week gehouden op vrijdagavond in Helsinki, Finland, om 21.00 uur lokale tijd (20.00 uur CET).','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4779,966,5,'De trekkingen van Mega Millions, een van de grootste Amerikaanse loterijen, worden twee keer per week gehouden in de Amerikaanse stad Atlanta, Georgia, elke dinsdag en vrijdag om 23:00 Eastern Time (dat is 05:00 op woensdag en zaterdagochtend voor Centraal-Europa Tijd).','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4780,968,5,'Superenalotto vindt drie keer per week plaats, elke dinsdag, donderdag en zaterdag, in Italië om 20:00 CET (19:00 GMT). Als de trekking samenvalt met een Italiaanse feestdag, wordt deze uitgesteld tot de volgende dag.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4781,970,5,'De Mega-sena is de meest populaire loterij in Brazilië en hun trekkingen worden twee keer per week gehouden, elke woensdag en zaterdag, in Brasilia om 20:00, lokale tijd (23:00 CET).','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4782,972,5,'De Lotto 6aus49 is de belangrijkste nationale loterij in Duitsland waarvan de trekking elke woensdag om 18:25 CET (17:25 GMT) en elke zaterdag om 19:25 uur CET (18:25 GMT) plaatsvindt.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4783,974,5,'Alle trekkingen van Thunderball, een van de meest populaire loterijen in het Verenigd Koninkrijk, worden elke woensdag, vrijdag en zaterdag om 20:00 lokale tijd (21:00 CET) gehouden.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4784,976,5,'De UK Lotto-trekking vindt drie keer per week plaats, elke woensdag-, vrijdag- en zaterdagavond, in het Verenigd Koninkrijk om 20:30 GMT (21:30 CET), direct na de Thunderball-trekking.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4785,949,5,'Wist je dat je de kans hebt om iedere dag van de week verschillende loterijen te winnen die in verschillende landen over de hele wereld worden plaatsvinden? Ontdek aan welke populaire loterijen je iedere dag van de week kan deelnemen en wanneer ze plaatsvinden. En vergeet niet dat elk jaar tijdens de kerstvakantie twee speciale trekkingen worden gehouden in Spanje: El Gordo Christmas Lottery op 22 december en El Niño Lottery op 6 januari!','nl');
