INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4792,938,5,'Vrijdag is een geluksdag voor veel loterijspelers van EuroMillions, omdat er op deze dag de SuperDraw plaatsvindt. Meer dan 80% van de winnaars van de EuroMillions-jackpot kochten hun kaartjes op deze vrijdag. De trekking zal zoals altijd om 21:00 CET plaatsvinden. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4793,936,5,'De ticket verkoop van EuroMillions sluit om 19.00 uur (CET) op dezelfde dag van de trekking. U kunt kaarten kopen voor de volgende trekking kort nadat de laatste trekking heeft plaatsgevonden. Mis niet de kans om onze volgende EuroMillionaire te worden en <a href=\'https://euromillions.com/euromillions/play\'> koop nu je tickets op EuroMillions.com </a>!','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4794,940,5,'Hoewel de kansen om te winnen op elke trekkingsdag hetzelfde zijn, kopen EuroMillions-spelers gewoonlijk minder kaartjes op dinsdagen. Juist om deze reden is de jackpot van EuroMillions in verschillende trekkingen op dinsdagnacht gewonnen door een enkel winnend ticket.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4795,932,5,'De trekkingen van Euromillions vinden twee keer per week plaats op dinsdag en vrijdag om 21:00 CET (20:00 GMT) in Parijs. In elke trekking worden 5 getallen en 2 Lucky Stars geselecteerd. De meest recente resultaten met winnende nummers van de loterij worden onthuld op onze <a href=\'/euromillions/results\'> EuroMillions-resultatenpagina </a> rond 22:30 CET (21:30 GMT) van de trekkingsdag.','nl');
