INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4817,1003,5,'Euromillions is de grootste Europese transnationale loterij, met elke dinsdag en vrijdag een minimum jackpot van €17 miljoen. Om de Euromillions jackpot te winnen, moet je 5 hoofdnummers en 2 Lucky Stars matchen. Om een andere prijs te winnen hoef je maar 2 of meer nummers overeen te hebben. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4818,1005,5,'Powerball is een van de grootste loterijen in de Verenigde Staten waarvan de trekking elke woensdag en zaterdag plaatsvindt. Deze loterij heeft een jackpot van minimaal $40 miljoen en deze wordt hoger tot er een winnaar is. Om mee te spelen moet je 6 nummers kiezen van 1 tot 69 en een powerball-nummer van 1 tot 26. Om een jackpot te winnen moeten alle nummers juist zijn. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4819,1007,5,'Eurojackpot is een van de meest populaire Europese loterijen, de trekkingen worden elke vrijdag gehouden met een minimale jackpot van €10 miljoen. Om mee te spelen moet je 5 hoofdnummers selecteren van 1 tot 50, en 2 euro-nummers van 1 tot 10. Om de jackpot te winnen moet je alle 5 nummers en 2 euro-nummers juist hebben.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4820,1009,5,'Mega Millions is een populaire Amerikaanse loterij waarvan de trekking op dinsdag en vrijdag plaatsvindt. De jackpot begint bij $40 miljoen en elke keer dat er geen jackpot-winnaar is, wordt er $5 miljoen aan de jackpot toegevoegd totdat er een winnaar is. Om mee te spelen moet je 5 nummers kiezen van 1 tot 70 en een Mega Ball van 1 tot 25. Om de jackpot te winnen, moet je de 6 nummers allemaal juist hebben. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4821,1011,5,'Mega-Sena is de meest populaire loterij in Brazilië en de trekkingen vinden elke woensdag en zaterdag plaats. Het heeft een minimale jackpot van R $2 miljoen (meer dan € 500.000), om te winnen moet je 6 juiste nummers van 1 tot 60 hebben. Om kleinere prijzen te winnen moeten spelers minstens 4 juiste nummers hebben. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4822,1013,5,'Superenalotto is de belangrijkste nationale loterij in Italië. De trekking vindt iedere dinsdag, donderdag en zaterdag plaats. De Superenalotto heeft een minimale jackpot van €1,3 miljoen en er is geen maximale jackpot. Daarom kan je als je mee speelt vaak kans maken op enorme de enorme jackpot. Om mee te spelen moet je 6 nummers kiezen van 1 tot 90 en je kan al prijzen winnen door minstens 2 juiste nummers overeen te hebben. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4823,1015,5,'La Primitiva is een van de meest gespeelde loterijen in Spanje, de trekkingen worden vinden plaats op donderdagen en zaterdagen. La Primitiva heeft een minimale jackpot van €2 miljoen en geen maximum. Om de jackpot te winnen moet je 6 hoofdnummers van 1 tot 49 en het restitutienummer tussen 0 en 9 juist hebben. Je kan daarnaast al prijzen winnen als je minstens 3 van de hoofdnummers juist hebt. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4824,1017,5,'De Ierse Lotto is een van de favoriete loterijen in Ierland. De trekking vindt iedere woensdag en zaterdag plaats. Deze loterij heeft een minimale jackpot van €2 miljoen en om te winnen moet de speler de 6 hoofdnummers van 1 tot 47 juist hebben. Daarnaast kan je je kansen vergroten om kleinere prijzen te winnen door een extra nummer te kiezen.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4825,1019,5,'De UK Lotto is een van de meest populaire loterijen in het Verenigd Koninkrijk. Op woensdagen is de minimale jackpot meestal £2 miljoen (bijna €2,4 miljoen) en op zaterdag £4 miljoen (meer dan €4,4 miljoen). Als niemand wint groeit de jackpot totdat deze een maximum van £22 miljoen (meer dan €24,67 miljoen) heeft bereikt (Dit kan veranderen als de overheid het belangrijk vindt dat de jackpot alsnog gewonnen moet worden). Om de jackpot te winnen moet je 6 hoofdnummers van 1 tot 59 juist hebben. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4826,998,5,'Waarom zou je alleen met de loterijen uit je eigen land mee spelen? Je kunt nu online mee spelen met de meest populaire loterijen uit de wereld en elke dag van de week kans maken op grote prijzen! Waar je ook bent, je speelt gemakkelijk online mee. Ontdek waar de grootste jackpots van de wereld te winnen zijn en misschien ben jij wel toekomstig miljonair! ','nl');
