INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5684,1139,1,'The odds of winning a prize on the Powerball are 1 in 24.87 and the odds of winning the jackpot are 1 in 292 201 338. Compare them with the <a href=\'https://euromillions.com/en/facts/top-lotteries--odds-of-winning\'>odds of winning other international lotteries</a>!','en');
