INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5749,1122,1,'Let your instincts guide you and pick five different numbers from 1 to 69; then select a special one, called Powerball number, from 1 to 26. Or you can play Randomize all lines and let the computer choose your numbers.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5750,1122,2,'Deja que tus instintos te guíen y escoge cinco diferentes números del 1 al 69; luego elige uno especial, llamado número Powerball, del 1 al 26. O puedes jugar con Números aleatorios para todas las líneas y dejar que el ordenador escoja tus números.','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5751,1122,3,'Пусть ваши инстинкты помогут вам выбрать пять разных чисел от 1 до 69; затем выберите специальный номер, называемый номером Powerball, от 1 до 26. Или вы можете воспроизвести Randomize all lines и позволить компьютеру выбрать ваши номера.','ru');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5752,1122,4,'Lasciati guidare dall\'istinto e scegli 5 numeri diversi dall\'1 al 69; poi scegline uno speciale, il numero Powerball, tra l\'1 e il 26. Oppure attiva il generatore di numeri random su tutte le linee e il computer sceglierà per te tutti i numeri in un click.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5753,1122,5,'Laat je leiden door je instinct en kies vijf verschillende nummers van 1 tot 69; selecteer vervolgens een speciaal Powerball-nummer, van 1 tot 26. Je kan ook willekeurig alle lijnen laten kiezen door de computer.  ','nl');
