INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5759,1124,1,'With EuroMillions.com you can add more lines and select serval combinations of numbers for the next draw, to increase your chances of winning, and you can decide how many of the following draws you want to play with the same tickets, so you don\'t have to bother selecting your numbers again.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5760,1124,2,'Con EuroMillions.com puedes añadir más líneas y seleccionar varias combinaciones de números para el siguiente sorteo, para aumentar tus probabilidades de ganar, y puedes decidir cuántos de los siguientes sorteos quieres jugar con los mismos boletos, así no tendrás que molestarte en seleccionar tus números de nuevo. ','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5761,1124,3,'С EuroMillions.com вы можете добавлять больше строк и выбирать комбинацию чисел для следующего розыгрыша для того, чтобы увеличить ваши шансы на победу. Вы так же можете решить, сколько из следующих игр вы хотите сыграть с одними и теми же билетами, тогда вам не придется беспокоиться о выборе номеров снова.','ru');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5762,1124,4,'Su EuroMillions.com puoi aggiungere più linee e slezionare varie combinazioni di numeri per la prossima estrazione e aumentare così le tue possibilità di vincere, e puoi anche decidere a quante delle prossime estrazioni vuoi partecipare con gli stessi biglietti, in modo da non doverli selezionare nuovamente ogni settimana.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5763,1124,5,'Met EuroMillions.com kun je om je winkansen te vergroten meer lijnen toevoegen en verschillende combinaties van getallen selecteren voor de volgende trekking. Je kan zelf bepalen met hoeveel trekkingen je wilt spelen met dezelfde nummers.','nl');
