INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5779,1128,1,'On completion of payment you will receive a message on the website confirming the numbers you have played and the draw you have entered. You can see all the lines and tickets you have purchased at any time in your player account.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5780,1128,2,'Al completar tu pago recibirás un mensaje confirmando los números que has jugado y el sorteo en el que has entrado. Puedes ver todas las líneas y boletos que has comprado en cualquier momento en tu cuenta de jugador.','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5781,1128,3,'По завершению платежа вы получите сообщение на веб-сайте, подтверждающее номера, которые вы выбрали, и розыгрыш, который вы ввели. Вы можете увидеть все строки и билеты, которые вы приобрели в любое время в своей учетной записи.','ru');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5782,1128,4,'Completato l\'acquisto, visualizzerai un messaggio con la conferma dei numeri da te giocati e l\'estrazione o le estrazioni a cui parteciperai. Puoi rivedere tutte le linee e i biglietti che hai comprato anche nel tuo account di giocatore, in qualsiasi momento.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5783,1128,5,'Na het voltooien van de betaling ontvang je een bericht op de website met daarin de gekozen nummers en de trekking die je hebt ingevoerd. Je kunt alle lijnen en tickets die je hebt gekocht op ieder moment in je spelersaccount terug zien.','nl');
