INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5790,1130,2,'Poco después del sorteo recibirás una notificación por correo electrónico detallando los últimos resultados y si has ganado. Todas las ganancias serán acreditadas automáticamente en tu cuenta de jugador.','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5791,1130,3,'Вскоре после розыгрыша вы получите уведомление по электронной почте, в котором будут указаны последние результаты, и выиграли ли вы. Любые выигрыши будут автоматически зачислены на ваш игровой счет.','ru');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (5792,1130,4,'Poco dopo l\'estrazione riceverai un\'email con il dettaglio degli ultimi risultati e  verrai avvisato nel caso tu abbia vinto. L\'ammontare dei premi verrà accreditato automaticamente sul tuo account di giocatore.','it');
