INSERT INTO `article_details` VALUES (31, 9, 'en', 'information_about_the_euromillions_lottery', 'Information about the EuroMillions lottery', '<table border="0" cellspacing="10" cellpadding="10">\r\n<tbody>\r\n<tr>\r\n<td bgcolor="f1f1f1"><a href="{{article_url::euromillions_history}}" target="_blank">\r\n <img src="{{base_url}}/templates/default/images/winners.png" width="245" title="Euromillion Winners" alt="the history of Euromillions"/>\r\n</td>\r\n<td></td>\r\n<td bgcolor="f1f1f1"><a href="http://play.euromillions.com/en/terms-and-conditions" target="_blank">\r\n <img src="{{base_url}}/templates/default/images/tandc.png" width="245" height="184" alt="Euromillions Terms and Conditions" title="Euromillions T&C"/>\r\n</a>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td bgcolor="f1f1f1"><h2>&nbsp;&nbsp;<a href="{{article_url::euromillions_history}}" title="Euromillions history"><strong>Euromillions History</strong></h2></a><p>&nbsp;&nbsp;&nbsp;About the history of Euromillions</p></td><td width="10">&nbsp;</td>\r\n<td bgcolor="f1f1f1"><h2>&nbsp;&nbsp;<a href="http://play.euromillions.com/en/terms-and-conditions" title="Euromillions T&Cs"><strong>Terms and Conditions</strong></h2></a><p> &nbsp;&nbsp; Terms and Conditions of Euromillions.com</p></td>\r\n</tr>\r\n<tr>\r\n<td >&nbsp;</td><td width="10">&nbsp;</td><td width="10">&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td bgcolor="f1f1f1"><a href="{{article_url::euromillions_rules}}" target="_blank">\r\n <img src="{{base_url}}/templates/default/images/rules.png" width="245" height="184" title="Euromillions rules" alt="click here to learn more about the rules of Euromillions"/>\r\n</td>\r\n<td></td>\r\n<td bgcolor="f1f1f1"><a href="http://play.euromillions.com/en/faq/" target="_blank">\r\n <img src="{{base_url}}/templates/default/images/faq.png" width="245" height="184" title="Euromillions FAQ" alt="Frequently asked questions about Euromillions"/>\r\n</a>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td bgcolor="f1f1f1"><h2>&nbsp;&nbsp;<a href="{{article_url