INSERT INTO `news_details` VALUES (162, 142, 'en', '44_million_euro_go_to_france_again', '44 Million Euro go to France, ...again!', '<p>For everybody who missed it and all the people living in an area where the Euromillions are not broadcasted on TV, let us start with the draw video</p>\r\n\r\n<H3> Draw Video</h3>\r\n<object id="flashObj" width="480" height="270" classid="clsid:D27CDB6E-AE6D-11cf-96B8-444553540000" codebase="http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=9,0,47,0"><param name="movie" value="http://c.brightcove.com/services/viewer/federated_f9?isVid=1&isUI=1" /><param name="bgcolor" value="#FFFFFF" /><param name="flashVars" value="videoId=2119586153001&linkBaseURL=http%3A%2F%2Fnltv.cdn.endemoluk.com%2Fvideos%2Fcategory%2Feuromillions-draws&playerID=796512125001&playerKey=AQ~~,AAAAuPCt6kE~,1959EwdtDB5zbir3IXaK5NNqj2Qti4NE&domain=embed&dynamicStreaming=true" /><param name="base" value="http://admin.brightcove.com" /><param name="seamlesstabbing" value="false" /><param name="allowFullScreen" value="true" /><param name="swLiveConnect" value="true" /><param name="allowScriptAccess" value="always" /><embed src="http://c.brightcove.com/services/viewer/federated_f9?isVid=1&isUI=1" bgcolor="#FFFFFF" flashVars="videoId=2119586153001&linkBaseURL=http%3A%2F%2Fnltv.cdn.endemoluk.com%2Fvideos%2Fcategory%2Feuromillions-draws&playerID=796512125001&playerKey=AQ~~,AAAAuPCt6kE~,1959EwdtDB5zbir3IXaK5NNqj2Qti4NE&domain=embed&dynamicStreaming=true" base="http://admin.brightcove.com" name="flashObj" width="570" height="315" seamlesstabbing="false" type="application/x-shockwave-flash" allowFullScreen="true" allowScriptAccess="always" swLiveConnect="true" pluginspage="http://www.macromedia.com/shockwave/download/index.cgi?P1_Prod_Version=ShockwaveFlash"></embed></object>\r\n\r\n<p><br>\r\nThe Result Numbers were:</p>\r\n<br>\r\n<p>\r\n                            <span class="greenball">3</span>\r\n                            <span class="greenbal